import React, { useState } from "react";
import { Link } from "react-router-dom";
const Buy = () => {
    const[home , setHome] = useState(true);
   // const[flag , setFlag] = useState(false);
    return ( <>
        {home ? (
        <div className="sectionn">
            <div className="innerSection">
        <form  className="container ">
            <h1 className="text-center">Login</h1>
            <div className="form-group ">
                <label htmlFor="email">Email</label>
                <input type="email" className="form-control text-center" name="email" placeholder="Enter Email"/>
            </div>
            <div className="form-group ">
                <label htmlFor="password">Password</label>
                <input type="password" className="form-control text-center" name="password" placeholder="Enter Password"/>
            </div>
            <button type="submit" className="btn btn-success w-25 d-block mx-auto mt-3">Login</button>
            {flag && (
                    <div className="alert alert-danger" role="alert">
                        Please Fill All Fields With Correct Data
                    </div>
                )}
                
        </form>
        </div>
        </div> 
        ) : (
            <div class="alert alert-success " role="alert">
                A simple success alertâ€”check it out!
                Now you can Back to <Link to='/' className="btn btn-outline-success" onClick={()=>{setTimeout(() => {
                    window.location.reload();
                }, 0);}}>Home</Link>
            </div>
            
        )
        }

    </> );
}

export default Buy;
