import React, { useState } from "react";
import { Link } from "react-router-dom";
const Buy = () => {
    const[flag , setFlag] = useState(false)
    const [email,setEmail] = useState("");
    const [address,setAddress] = useState("");
    const [phone,setPhone] = useState("");
    const[home , setHome] = useState(true);
    const handleSubmit = (e) => {
        e.preventDefault();

        if( !email || !address || !phone){
            setFlag(true);
        }
        
    }
   
    return ( <>
    {home ? (
        <div className="sectionn">
           <div className="innerSection">
        <form onSubmit={handleSubmit}  className="container  ">
            <h4 className="text-center">Shipping Adress</h4>
            <div className="form-group ">
                <label htmlFor="email">Email</label>
                <input type="email" className="form-control text-center" name="email" placeholder="Enter Email" />
            </div>
            <div className="form-group ">
                <label htmlFor="text">Adress</label>
                <input type="text" className="form-control text-center" name="address" placeholder="Enter Adress"/>
            </div>
            <div className="form-group my-2">
                    <label htmlFor="Phone">Phone No.</label>
                    <input type="phone" className="form-control" name="Phone" placeholder="Enter Your Number" />
                </div>
                <Link >
            <button type="submit" className="btn btn-success w-25 d-block mx-auto mt-3">Save</button>
            </Link>
            {flag && (
                    <div className="alert alert-danger" role="alert">
                        Please Fill All Fields
                    </div>
                )}
                
        </form>
        </div>
    </div> 
    ):(
        <div class="alert alert-success " role="alert">
            A simple success alertâ€”check it out!
            Now you can Back to <Link to='/' className="btn btn-outline-success" onClick={()=>{setTimeout(() => {
                window.location.reload();
            }, 0);}}>Home</Link>
        </div>
        
    )
    }

    </> );
}

export default Buy;
