import { Component } from "react";
import { Link } from 'react-router-dom';

class Edit extends Component {
    state = {
        txtId: "",
        txtName: this.props.location.SelectedObject.Name,
        txtAge: this.props.location.SelectedObject.Age,
    };
    InputHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    componentWillMount() {
        console.log(this.props.location);
        this.setState({
            txtId: this.props.location.SelectedObject.id,
        });
    }
    CustomEditHandler = () => {
        let EditObject = {
            id: this.state.txtId,
            Name: this.state.txtName,
            Age: this.state.txtAge,
        };
        this.props.EditHandlerRef(EditObject);
    };
    render() {
        console.log("Edit",this);
        return (
            <div className="container">
            <form>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">ID</label>
                    <input
                        type="text"
                        class="form-control"
                        name="txtId"
                        value={this.state.txtId}
                        onChange={this.InputHandler}
                        disabled/>
                </div>
                <div class="mb-3">
                    <label class="form-label">Name</label>
                    <input
                        type="text"
                        class="form-control"
                        name="txtName"
                        value={this.state.txtName}
                        onChange={this.InputHandler}/>
                </div>
                <div class="mb-3">
                    <label class="form-label">Age</label>
                    <input
                        type="text"
                        class="form-control"
                        name="txtAge"
                        value={this.state.txtAge}
                        onChange={this.InputHandler}/>
                    </div>
                <Link
                    to="/StudentList"
                    className="btn btn-success"
                    onClick={this.CustomEditHandler}>
                    SavingEdit
                </Link>
            </form>
            </div>
        );
    }
}  

export default Edit;
