import { Component } from "react";
class Add extends Component {

    CustomAdd = (props) => {
        let id = document.querySelector("#txtID").value;
        let name = document.querySelector("#txtName").value;
        let age = document.querySelector("#txtAge").value;
        let salary = document.querySelector("#txtsalary").value;
        let course = document.querySelector("#txtcourse").value;
        let email = document.querySelector("#txtEmail").value;
        let newObject = { id, name ,age,salary,course,email,imageUrl:"square.jpg"};
        props.AddEmpRefHandler(newObject);
    }
    render(){
        let styleForm ={
            padding:'10px',
            margin:'10px'
        }
        return (
            <>
            <input className="form-control" style={styleForm} placeholder="ID" id='txtID' />
            <input className="form-control" style={styleForm} placeholder="Name" id='txtName' />
            <input className="form-control" style={styleForm} placeholder="Age" id='txtAge' />
            <input className="form-control" style={styleForm} placeholder="Salary" id='txtsalary' />
            <input className="form-control" style={styleForm} placeholder="Course" id='txtcourse' />
            <input className="form-control" style={styleForm} placeholder="email" id='txtEmail' />
            <input type={'button'}style={styleForm} value='AddnewEmp' className="btn btn-success"onClick={this.CustomAdd}/>
        </>
    )
}
}
export default Add;
