import React from "react";
// import App from './App';
import Add from "./AddEmp";
import EmpList from "./EmpList";

export default class AppClass extends React.Component{
    constructor() {
        super();
        this.state = {
            EmpArray: [
                {id:1,name:'ahmed',age:26,salary:7000,course:'React',email:'anything@yourdomain'},
                {id:2,name:'ali',age:28,salary:9000,course:'vue',email:'anything@yourdomain'},
                {id:3,name:'mahmoud',age:24,salary:5000,course:'React',email:'anything@yourdomain'},
                {id:4,name:'saad',age:30,salary:15000,course:'vue',email:'anything@yourdomain'},
                {id:5,name:'ibrahim',age:20,salary:4000,course:'React',email:'anything@yourdomain'},
                {id:6,name:'mohamed',age:29,salary:11000,course:'vue',email:'anything@yourdomain'}
            ]
        }
    }
    RemoveHanlder = (_selectIndex) => {
        this.state.EmpArray.splice(_selectIndex, 1);
        this.setState({
            EmpArray: this.state.EmpArray
        })
    }
    EditHanlder = (Emp) =>{
        const EmpArray = [...this.state.EmpArray];
        EmpArray[Emp] = {...EmpArray[Emp]};
        EmpArray[Emp].id = document.querySelector("#txtID").value;
        EmpArray[Emp].name = document.querySelector("#txtName").value;
        EmpArray[Emp].age = document.querySelector("#txtAge").value;
        EmpArray[Emp].salary = document.querySelector("#txtsalary").value;
        EmpArray[Emp].course = document.querySelector("#txtcourse").value;
        EmpArray[Emp].email = document.querySelector("#txtEmail").value;
        this.setState({EmpArray});
    }
    AddnewEmp = (NewEmp) => {
        this.state.EmpArray.push(NewEmp);
        let newTR = ` <tr>
        <th scope="row">${NewEmp.id}</th>
        <td>${NewEmp.name}</td>
        <td>${NewEmp.age}</td>
        <td>${NewEmp.salary}</td>
        <td>${NewEmp.course}</td>
        <td>${NewEmp.email}</td>
        <td><img src="/public/square.jpg" alt="user" /></td>
        <td><button class="btn btn-outline-danger" onclick="${this.RemoveHanlder}" >Remove</button></td>
        <td><button class="btn btn-outline-primary" onClick="${this.EditHanlder}" >Edit</button></td>
        </tr>`;
        document.querySelector("tbody").innerHTML += newTR;
        // this.setState({EmpArray:this.state.EmpArray.push(NewEmp)})
    }
    render(){
        let styleH = {background:'greenyellow',padding:'20px',margin:'20px'}
        return(
            <>
                <h1 style={styleH}>Task 2</h1>
                <EmpList EmpLRef={this.state.EmpArray} RemoveRef={this.RemoveHanlder} EditRef={this.EditHanlder} />
                <Add AddEmpRefHandler={this.AddnewEmp}></Add>
                <img src="square.jpg" alt="user"/>
            </>
        )
    }
}
