import React, { useState } from "react";
import { Link } from "react-router-dom";
const Buy = () => {
    const[flag , setFlag] = useState(false)
    const [email,setEmail] = useState("");
    const [address,setPassword] = useState("");
    const [phone,setPhone] = useState("");
    //const[home , setHome] = useState(true);
    const handleSubmit = (e) => {
        e.preventDefault();

        if( !email || !password || !phone){
            setFlag(true);
        }else{
            setFlag(false);
            
            localStorage.setItem('Email',JSON.stringify(email));
            
            
        }
    }
   
    return ( <>
    
        <div className="sectionn">
           <div className="innerSection">
        <form  className="container  ">
            <h4 className="text-center">Shipping Adress</h4>
            <div className="form-group ">
                <label htmlFor="email">Email</label>
                <input type="email" className="form-control text-center" name="email" placeholder="Enter Email" />
            </div>
            <div className="form-group ">
                <label htmlFor="text">Adress</label>
                <input type="password" className="form-control text-center" name="address" placeholder="Enter Adress"/>
            </div>
            <div className="form-group my-2">
                    <label htmlFor="Phone">Phone No.</label>
                    <input type="phone" className="form-control" name="Phone" placeholder="Enter Your Number" />
                </div>
            <button type="submit" className="btn btn-success w-25 d-block mx-auto mt-3">Save</button>
            {flag && (
                    <div className="alert alert-danger" role="alert">
                        Please Fill All Fields
                    </div>
                )}
                
        </form>
        </div>
       </div> 
        

    </> );
}

export default Buy;
