let Add = (props) => {

    let CustomAdd = () => {
        let id = document.querySelector("#txtID").value;
        let Name = document.querySelector("#txtName").value;
        let age = document.querySelector("#txtAge").value;
        let salary = document.querySelector("#txtsalary").value;
        let course = document.querySelector("#txtcourse").value;
        let email = document.querySelector("#txtEmail").value;
        let imageSrc = document.querySelector("#txtimage").value;
        let newObject = { id, Name ,age,salary,course,email,imageSrc};

        props.AddStdRefHandler(newObject);
    }

    return (
        <>
            <input className="form-control" placeholder="ID" id='txtID' />
            <input className="form-control" placeholder="Name" id='txtName' />
            <input type={'button'} value='AddnewEmp' className="btn btn-success"/>
        </>
    )
}

export default Add;
