import EmpList from './EmpList';
import Add from './AddEmp';
let App = ()=>{
    let listemp = [
        {id:1,name:'ahmed',age:26,salary:7000,course:'React',email:'anything@yourdomain',EmpImage:'200x200.png'},
        {id:2,name:'ali',age:28,salary:9000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'},
        {id:3,name:'mahmoud',age:24,salary:5000,course:'React',email:'anything@yourdomain',EmpImage:'./200x200.png'},
        {id:4,name:'saad',age:30,salary:15000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'},
        {id:5,name:'ibrahim',age:20,salary:4000,course:'React',email:'anything@yourdomain',EmpImage:'./200x200.png'},
        {id:6,name:'mohamed',age:29,salary:11000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'}
    ];

    let AddnewEmp = (NewEmp) => {
        listemp.push(NewEmp);
        let newTR = ` <tr>
        <th scope="row">${NewEmp.id}</th>
        <td>${NewEmp.Name}</td>
        <td>${NewEmp.age}</td>
        <td>${NewEmp.salary}</td>
        <td>${NewEmp.course}</td>
        <td>${NewEmp.email}</td>
        <td><img src=${NewEmp.EmpImage} style={{width: '80px',height: '80px'}} alt={'userimg'}/></td>
        <td><button class="btn btn-outline-danger" >Remove</button></td>
        <td><button class="btn btn-outline-primary">Edit</button></td>
        </tr>`;
        document.getElementById("tblData").innerHTML += newTR;
    }
    return (
        <div>
            <div>
            <img src="https://www.bbc.com/news/in-pictures-56211135" style={{width:'100%'}}alt=""/>
            </div>
            <EmpList EmpLRef={listemp}/>
            <Add AddEmpRefHandler={AddnewEmp}></Add>
        </div>
    )
}
export default App
