    state = {
        EmpL: null,
        JSONplaceHolderArray: null,
    };

    render() {
        return(
            <React.Fragment>
                <Nav></Nav>
                <BrowserRouter>
                    <div className='container-fluid'>
                        {/* <Route  path="/EmpList" component={EmpList}/>
                        <Route  path="/AddEmp" component={AddEmp}/> */}
                    </div>
                </BrowserRouter>
            </React.Fragment>
        )
    }
}

export default App ;
