import React,{Link} from 'react-router-dom';

const EmpList = (props) => {

    let CustomeRemove = (_idexref) => {
        let Check = window.confirm("Are YOu Sure");
        if (Check) {
            props.RemoveHandlerRef(_idexref);
        }
    };
    return(
        <div className="container">
            <table class="table table-dark">
                <thead>
                    <tr>
                        <th scope="col">Serial</th>
                        <th scope="col">Id</th>
                        <th scope="col">Name</th>
                        <th scope="col">Age</th>
                        <th scope="col">Delete</th>
                        <th scope="col">Edit</th>
                    </tr>
                </thead>
                <tbody>
                {props.EmpArrayRef.map((item, index) => {
                    return (
                    <tr>
                        <th scope="row">{index + 1}</th>
                        <td>{item.id}</td>
                        <td>{item.Name}</td>
                        <td>{item.Age}</td>
                        <td>
                        <input
                            type="button"
                            className="btn btn-outline-danger"
                            value="Remove"
                            onClick={() => CustomeRemove(item.id)}
                        />
                        </td>
                        <td>
                            <Link
                                className="btn btn-outline-info"
                                to={{
                                pathname: `/Edit/${item.id}`,
                                SelectedObject:item
                                }}>
                                Edit
                            </Link>
                        </td>
                        </tr>
                        );
                    })}
                </tbody>
            </table>
            <Link to="/AddEmp" className="btn btn-success">
                AddnewStd
            </Link>
        </div>
    );
};

export default EmpList ;
