import {Component} from 'react';
import {BrowserRouter, Route, Link} from 'react-router-dom';

import Nav from './Nav';
import EmpList from './EmpList';
import AddEmp from './AddEmp';
import EditEmp from './EditEmp';

class App extends Component{
    state = {
        StdList: null,
        JSONplaceHolderArray: null,
    };

    RemoveHandler = (_index) => {
        this.state.StdList.splice(_index, 1);
        this.setState({
        StdList: this.state.StdList,
        });
    };

    AddHanlder = (_newObject) => {
        let StdList = [...this.state.StdList, _newObject];
        this.setState({
        StdList,
        });
    };

    EditHandler = (SelectedObject) => {
        let Search = this.state.StdList.find((item) => {
        return item.id == SelectedObject.id;
        });
        Search.Name = SelectedObject.Name;
        Search.Age = SelectedObject.Age;
        this.setState({
        StdList: this.state.StdList,
        });
    };
    render() {
        if(this.state.EmpList != null){
            return(
                    <BrowserRouter>
                        <Nav/>
                        <Route Component={() =>{
                            return(
                                <EmpList EmpArrayRef={this.state.EmpList} />
                            );
                        }}
                        path='/' exact/>

                        <Route Component={() =>{
                            return(
                                <EmpList EmpArrayRef={this.state.EmpList}
                                RemoveHandlerRef ={this.RemoveHandler}/>
                                );
                        }}
                        path='/EmpList'/>

                        <Route component={(props) => {
                            console.log("Test Function Anonouys Route props", props);
                            return (
                                <div>
                                    <h2>Test Rout Component</h2>
                                </div>
                            );
                            }}
                        path="/TestCom"/>

                        <Route 
                            component={(props) => (
                                <AddEmp {...props} AddHanlderRef={this.AddHanlder} />
                            )}
                            path="/StudentAdd"/>
                        <Route 
                            component={(props) => (
                                <EditEmp {...props}
                                StdListRef={this.state.StdList}
                                EditHandlerRef={this.EditHandler}
                                />
                            )}
                        path="/Edit/:id"/>
                    </BrowserRouter>
            )
        }
    }
}


export default App ;
