import React from "react";
// import App from './App';
import Add from "./AddEmp";
import EmpList from "./EmpList";

export default class AppClass extends React.Component{
    constructor() {
        super();
        this.state = {
            EmpArray: [
                {id:1,name:'ahmed',age:26,salary:7000,course:'React',email:'anything@yourdomain',EmpImage:'200x200.png'},
                {id:2,name:'ali',age:28,salary:9000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'},
                {id:3,name:'mahmoud',age:24,salary:5000,course:'React',email:'anything@yourdomain',EmpImage:'./200x200.png'},
                {id:4,name:'saad',age:30,salary:15000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'},
                {id:5,name:'ibrahim',age:20,salary:4000,course:'React',email:'anything@yourdomain',EmpImage:'./200x200.png'},
                {id:6,name:'mohamed',age:29,salary:11000,course:'vue',email:'anything@yourdomain',EmpImage:'./200x200.png'}
            ]
        }
    }
    RemoveHanlder = (_selectIndex) => {
        const EmpArray = this.state.EmpArray.filter( (E) => E.id !== _selectIndex.id)
        this.setState({EmpArray})
    }
    EditHanlder = () =>{
        let EditObject = {
            id: this.state.EmpArray.id,
            name: this.state.EmpArray.name,
            age: this.state.EmpArray.age,
            salary: this.state.EmpArray.salary,
            course: this.state.EmpArray.course,
            email: this.state.EmpArray.email
        }
        this.props.EditRef(EditObject);

    }
    AddnewEmp = (NewEmp) => {
        this.state.EmpArray.push(NewEmp);
        let newTR = ` <tr>
        <th scope="row">${NewEmp.id}</th>
        <td>${NewEmp.name}</td>
        <td>${NewEmp.age}</td>
        <td>${NewEmp.salary}</td>
        <td>${NewEmp.course}</td>
        <td>${NewEmp.email}</td>
        <td><img src={${NewEmp.EmpImage}} style={{width: '80px',height: '80px'}} alt={'userimg'}/></td>
        <td><button class="btn btn-outline-danger" onClick= this.parentElement.parentElement.remove();>Remove</button></td>
        <td><button class="btn btn-outline-primary">Edit</button></td>
        </tr>`;
        document.getElementById("tblData").innerHTML += newTR;
    }
    render(){
        let styleH = {background:'greenyellow',padding:'20px',margin:'20px'}
        return(
            <>
                <h1 style={styleH}>Task 2</h1>
                <EmpList EmpLRef={this.state.EmpArray} RemoveRef={this.RemoveHanlder} EditRef={this.EditHanlder} />
                <Add AddEmpRefHandler={this.AddnewEmp}></Add>
            </>
        )
    }
}
