import { Component } from "react";
import { Link } from "react-router-dom";

export default class AddEmp extends Component {
    state ={
        txtId: "",
        txtName: "",
        txtAge: "",
    }
    backtOList = () => {
        this.props.AddHanlderRef({ id: this.state.txtId, Name: this.state.txtName, Age: this.state.txtAge });
        this.props.history.push("/StudentList");
    };
    InputHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };
    render() {
        return (
            <form>
                <div class="mb-3">
                    <label for="exampleInputEmail1" class="form-label">ID</label>
                    <input type="text"
                        class="form-control"
                        name="txtId"
                        value={this.state.txtId}
                        onChange={this.InputHandler}/>
                </div>
                <div class="mb-3">
                    <label class="form-label">Name</label>
                    <input
                        type="text"
                        class="form-control"
                        name="txtName"
                        value={this.txtName}
                        onChange={this.InputHandler}/>
                </div>
                <div class="mb-3">
                    <label class="form-label">Age</label>
                    <input
                        type="text"
                        class="form-control"
                        name="txtAge"
                        value={this.txtAge}
                        onChange={this.InputHandler}/>
                </div>
                <button type="button" class="btn btn-primary" onClick={this.backtOList}>Save</button>
                <Link
                    to="/EmpList"
                    class="btn btn-outline-success"
                    onClick={this.BackTOList}>
                    BackTOListCom
                </Link>
            </form>
        );
    }
}
